package org.xmgreat.bean;

public class TriMouthBean {
         private int onemouth;
         private int twomouth;
         private int trimouth;
         public TriMouthBean() {
			// TODO Auto-generated constructor stub
		}
         public TriMouthBean(int onemouth, int twomouth, int trimouth) {
			super();
			this.onemouth = onemouth;
			this.twomouth = twomouth;
			this.trimouth = trimouth;
		}
		public int getOnemouth() {
			return onemouth;
		}
		public void setOnemouth(int onemouth) {
			this.onemouth = onemouth;
		}
		public int getTwomouth() {
			return twomouth;
		}
		public void setTwomouth(int twomouth) {
			this.twomouth = twomouth;
		}
		public int getTrimouth() {
			return trimouth;
		}
		public void setTrimouth(int trimouth) {
			this.trimouth = trimouth;
		}
		
         
}
