mapper.select:
select * from(select p.parametername,u.* ,rownum rn from tblUserInfo
u,tblparameter p

	
		and userName like concat(concat('%',#{userName}),'%')
		
	
	
		and phyCardId = #{phyCardId1}
	
	
		and u.parameterID = #{parameterID1}
	
	
		and regTime >=#{regTimeA}
	
	
		and regTime <=#{regTimeB}
	
	and u.parameterID=p.parameterID and u.parameterID!=0 and rownum
	<#{page}*3+1 ORDER BY userId

) where rn >(#{page} - 1) * 3

select count(*) from tblUserInfo

	
		and userName like concat(concat('%',#{userName}),'%')
		
	
	
		and phyCardId = #{phyCardId1}
	
	
		and parameterID = #{parameterID1}
	
	
		and regTime >=#{regTimeA}
	
	
		and regTime <=#{regTimeB}
	
	and parameterId != 0

select * from(select p.parametername,r.roleName,o.officename,a.*
,rownum rn from tblManager a,tblparameter p,tblRole r,tbloffice o

	
		and managerName like
			concat(concat('%',#{mangerName}),'%')
	
	
		and phoneNum = #{phoneNum1}
	
	
		and a.parameterID = #{paramterId1}
	
	
		and r.roleId =#{roleId1}
	
	
		and o.officeId =#{officeId1}
	
	and a.parameterID=p.parameterID and a.parameterID!=0 and
	a.roleId=r.roleId and a.officeid=o.officeid and a.roleId!=1 and
	rownum <#{page}*3+1 ORDER BY adminId

) where rn >(#{page} - 1) * 3

		select count(*) from tblManager
		
			
				and managerName like
					concat(concat('%',#{mangerName}),'%')
			
			
				and phoneNum = #{phoneNum1}
			
			
				and parameterID = #{paramterId1}
			
			
				and roleId =#{roleId1}
			
			
				and officeId =#{officeId1}
			
			and parameterId != 0

select * from(select p.parametername,h.* ,rownum rn from TBLPHYCARD
h,tblparameter p

	
		and PHYCARDID = #{phyCardId1}
	
	
		and h.parameterID = #{parameterId1}
	
	and h.parameterID=p.parameterID and h.parameterID!=16 and
	h.parameterID!=0 and rownum <#{page}*3+1 ORDER BY phyCardId

) where rn >(#{page} - 1) * 3

select count(*) from TBLPHYCARD

	
		and PHYCARDID = #{phyCardId1}
	
	
		and parameterID = #{parameterId1}
	
	and parameterId != 16 and parameterId != 0

select * from( select * from( select rownum rn, line.* from( select u.userid, u.username, u.phycardid, p1.parameterid paramId1, p1.parametername paramname1,p2.parameterid paramId2, p2.parametername paramname2 from tbluserinfo u, tblparameter p1, tblparameter p2, tblphycard p
and u.userId = #{userId1}
and userName like concat(concat('%',#{userName}),'%')
and p.phyCardId = #{phyCardId1}
and u.parameterid = p1.parameterid and u.phycardid = p.phycardid(+) and p.parameterid = p2.parameterid(+) ORDER BY u.USERID
)line)line1 where line1.rn <#{page}*3+1)line2 where line2.rn >(#{page} - 1) * 3

select * from TBLUSERACCOUT where occurtime = (
select max(occurtime) from TBLUSERACCOUT where userid = #{userId} ) and
userid = #{userId}
