mapper.select:
select * from ( select * from ( select rownum rn, r.* from ( select p1.*, p2.parametername typeName from tblparameter p1, tblparameter p2 where p1.parametertype >0 and p1.parametertype = p2.parameterid
and p2.parametername = #{typeName}
order by p2.parametername ) r ) r1 where r1.rn <= #{pageNo}*5 ) r2 where r2.rn > (#{pageNo}-1)*5

choose.when:
update tblparameter set parameterName = #{parameterName}, parameterValues
= #{parameterValues}
where parameterId = #{parameterId}
